import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Webcam from "react-webcam";

// register member's face for face login
const FaceRegister = () => {
    const [process, setProcess] = useState("");
    const countRef = useRef(0);
    const webcamRef = useRef(0);
    const videoConstraints = {
        width: 200,
        height: 200,
        facingMode: "user",
    };

    const checkFace = () => {
        setTimeout(() => {
            const imageSrc = webcamRef.current.getScreenshot();
            if (imageSrc) {
                axios
                    .post("http://127.0.0.1:5000/register", {
                        data: imageSrc,
                    })
                    .then((resp) => {
                        console.log("check: " + resp.data);
                        setProcess("확인 중입니다");
                        if (resp.data === "checked")
                            window.location.replace(
                                "http://127.0.0.1:8080/react/register"
                            );
                        else checkFace();
                    });
            } else checkFace();
        }, 100);
    };

    const newFace = () => {
        setTimeout(() => {
            countRef.current++;
            console.log(countRef.current);
            const imageSrc = webcamRef.current.getScreenshot();
            if (imageSrc) {
                axios
                    .post("http://127.0.0.1:5000/register", {
                        data: imageSrc,
                        key: countRef.current,
                    })
                    .then((resp) => {
                        console.log("register data: " + resp.data);
                        setProcess("등록 중입니다");
                        if (resp.data === "fail") newFace();
                        else checkFace();
                    });
            } else newFace();
        }, 100);
    };
    useEffect(() => {
        newFace();
    }, []);

    return (
        <div>
            <Webcam
                mirrored={true}
                audio={false}
                height={300}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                width={350}
                videoConstraints={videoConstraints}
            />
            <h2>{process}</h2>
        </div>
    );
};

export default FaceRegister;
